MINE
nigix comes with default MINE types
location
alias
try_files
rewrite and redirect

http {
    # MINE teypes
    #types {
    #    text/css css;
    #    text/html html;
    #}

    include mime.types

    # server
    server {
        listen 80;
        root /path;

        # get locations
        location /fruits{
            root /path ;
        }

        location /carbs{
            alias /path/fruits;

        }

        # try files
        location /veges{
            root /path;
            try_files /veges/vege.html /veges/index.html = 404;
        }

        # count regular expression ~* => regular expression
        location ~* /count/[0-9]{
            root /path
            try_files /index.html =404;
        }

        # return
        location /crops {
            return 307 /fruits;
        }

        # rewrite/redirect
        rewrite ^/number/(\w+) /counts/$1
    }
}
